INSERT INTO question (question_text, answer, author) VALUES
    ('Что такое JVM?', 'JVM (Java Virtual Machine) — виртуальная машина, которая исполняет байткод Java и обеспечивает независимость от платформы.', 'Wersatil'),
    ('Что такое JRE?', 'JRE (Java Runtime Environment) — среда выполнения Java, включающая JVM и стандартные библиотеки.', 'Wersatil'),
    ('Что такое JDK?', 'JDK (Java Development Kit) — набор инструментов для разработки Java-приложений, включает JRE и компилятор.', 'Wersatil'),
    ('Чем отличается JDK от JRE?', 'JDK предназначен для разработки и содержит инструменты, JRE — только для запуска приложений.', 'Wersatil'),
    ('Можно ли запустить Java-программу без JRE?', 'Нет, для выполнения Java-программы требуется JRE или JDK (в состав которого входит JRE).', 'Wersatil'),
    ('Входит ли JVM в JDK?', 'Да, JVM является частью JRE, а JRE входит в JDK.', 'Wersatil'),
    ('Зачем нужны обёртки?', 'Они нужны для работы с коллекциями, null и обобщениями.', 'Wersatil'),
    ('Что такое автоупаковка и автораспаковка?', 'Автоупаковка — автоматическое преобразование примитива в объект-обёртку. Автораспаковка — автоматическое преобразование обёртки в примитив.', 'Wersatil'),
    ('В чем заключается суть контракта equals/hashCode?', 'Равные объекты по equals() должны иметь одинаковый hashCode().', 'Wersatil'),
    ('Почему String immutable?', 'Для безопасности, кеширования и оптимизации памяти.', 'Wersatil'),
    ('Что такое String Pool?', 'String Pool — область памяти, где хранятся строковые литералы.', 'Wersatil'),
    ('Чем отличается StringBuilder от String?', 'StringBuilder изменяемый и более производительный при конкатенации.', 'Wersatil'),
    ('Чем отличается StringBuilder от StringBuffer?', 'StringBuffer потокобезопасен, StringBuilder — нет.', 'Wersatil'),
    ('Можно ли использовать this в static-методе?', 'Нет, static-методы не связаны с объектом.', 'Wersatil'),
    ('Для чего можно использовать this?', 'this — ссылка на текущий объект. она используется для доступа к полям объекта и устранения неоднозначности.', 'Wersatil'),
    ('Для чего можно использовать super() и когда он вызывается автоматически?', 'super — ссылка на объект родительского класса. Для вызова конструктора родительского класса. При создании объекта дочернего класса он вызывается автоматически.', 'Wersatil'),
    ('Можно ли обращаться к private-полям через super?', 'Нет, private-поля недоступны.', 'Wersatil'),
    ('Может ли класс быть final?', 'Да, тогда от него нельзя наследоваться.', 'Wersatil'),
    ('Может ли static-метод быть переопределён?', 'Нет, static-методы могут быть только скрыты.', 'Wersatil'),
    ('Перечислите методы класса Object.', 'equals() и hashcode(). ToString() и getClass(). Notify(), notifyAll() и wait().', 'Wersatil');
