INSERT INTO question (question_text, answer, author) VALUES
    ('Что такое checked-исключения?', 'Checked-исключения — это исключения, которые проверяются компилятором и должны быть обработаны или объявлены через throws.', 'Wersatil'),
    ('Что такое unchecked-исключения?', 'Unchecked-исключения — это RuntimeException и его подклассы, их можно не обрабатывать.', 'Wersatil'),
    ('Примеры checked-исключений?', 'IOException, SQLException, ClassNotFoundException.', 'Wersatil'),
    ('Примеры unchecked-исключений?', 'NullPointerException, ArrayIndexOutOfBoundsException, IllegalArgumentException.', 'Wersatil'),
    ('Что произойдет, если checked-исключение не обработано?', 'Компилятор выдаст ошибку, код не скомпилируется.', 'Wersatil'),
    ('Что произойдет, если unchecked-исключение не обработано?', 'Программа может упасть во время выполнения.', 'Wersatil'),
    ('Что такое try?', 'Блок try содержит код, который может вызвать исключение.', 'Wersatil'),
    ('Что такое catch?', 'Блок catch перехватывает и обрабатывает исключения, выброшенные в try.', 'Wersatil'),
    ('Что такое finally?', 'Блок finally выполняется всегда после try/catch, даже если было исключение.', 'Wersatil'),
    ('Можно ли использовать try без catch?', 'Да, если есть блок finally.', 'Wersatil'),
    ('Можно ли использовать try с catch без finally?', 'Да, catch достаточно для обработки исключения.', 'Wersatil'),
    ('Можно ли несколько catch для одного try?', 'Да, можно перехватывать разные типы исключений.', 'Wersatil'),
    ('Что такое multi-catch?', 'Использование одного catch для нескольких типов исключений через |.', 'Wersatil'),
    ('Что такое throw?', 'throw используется для явного выбрасывания исключения.', 'Wersatil'),
    ('Что такое throws?', 'throws используется в объявлении метода для указания возможных исключений.', 'Wersatil'),
    ('Разница между throw и throws?', 'throw — выбрасывает исключение, throws — объявляет его для метода.', 'Wersatil'),
    ('Можно ли throw несколько исключений одновременно?', 'Нет, throw выбрасывает одно исключение.', 'Wersatil'),
    ('Можно ли throws перечислять несколько исключений?', 'Да, через запятую.', 'Wersatil'),
    ('Что такое пользовательское исключение?', 'Это класс, который наследуется от Exception или RuntimeException.', 'Wersatil'),
    ('Когда создавать своё исключение?', 'Когда нужно сигнализировать о специфической ошибке, не покрытой стандартными классами.', 'Wersatil'),
    ('Как создать checked-исключение?', 'Наследовать от Exception и определить конструктор.', 'Wersatil'),
    ('Как создать unchecked-исключение?', 'Наследовать от RuntimeException.', 'Wersatil'),
    ('Можно ли переопределять методы с throws?', 'Да, но подкласс может выбрасывать только подтипы исключений, указанных в родителе.', 'Wersatil'),
    ('Можно ли убрать throws при переопределении?', 'Да, если метод не выбрасывает checked-исключения.', 'Wersatil'),
    ('Можно ли ловить unchecked-исключения?', 'Да, можно, но не обязательно.', 'Wersatil'),
    ('В каком порядке писать catch?', 'От более специфичных исключений к более общим.', 'Wersatil'),
    ('Что произойдет, если catch идет от общего к специфическому?', 'Компилятор выдаст ошибку unreachable code.', 'Wersatil'),
    ('Можно ли использовать finally без try?', 'Нет, finally всегда связан с try.', 'Wersatil'),
    ('Что произойдет, если в finally есть return?', 'Возвращаемое значение из finally перекрывает return из try/catch.', 'Wersatil'),
    ('Можно ли выбрасывать null через throw?', 'Нет, это вызовет NullPointerException.', 'Wersatil'),
    ('Можно ли внутри catch снова выбросить исключение?', 'Да, используя throw.', 'Wersatil'),
    ('Можно ли выбросить checked-исключение без throws?', 'Нет, компилятор выдаст ошибку.', 'Wersatil'),
    ('Что делает getMessage() у исключения?', 'Возвращает текстовое сообщение, переданное при создании исключения.', 'Wersatil'),
    ('Что делает printStackTrace()?', 'Выводит стек вызовов до момента выброса исключения.', 'Wersatil'),
    ('Можно ли наследовать несколько исключений одновременно?', 'Нет, Java не поддерживает множественное наследование классов.', 'Wersatil'),
    ('Почему RuntimeException называют unchecked?', 'Потому что компилятор не требует обработки.', 'Wersatil'),
    ('Можно ли throw RuntimeException без try/catch?', 'Да, это допустимо.', 'Wersatil'),
    ('Можно ли в блоке catch перехватывать несколько исключений?', 'Да, начиная с Java 7 можно использовать multi-catch.', 'Wersatil'),
    ('Можно ли создать исключение без сообщения?', 'Да, можно использовать конструктор по умолчанию.', 'Wersatil'),
    ('Можно ли вызвать super() в своём исключении?', 'Да, чтобы передать сообщение или причину в родительский класс.', 'Wersatil'),
    ('Что такое chained exceptions?', 'Когда одно исключение хранит ссылку на другое через initCause().', 'Wersatil'),
    ('Как получить тип исключения?', 'Использовать getClass().getName() или instanceof.', 'Wersatil'),
    ('Почему finally важен при работе с ресурсами?', 'Он гарантирует освобождение ресурсов, даже если произошло исключение.', 'Wersatil'),
    ('Можно ли использовать try-with-resources вместо finally?', 'Да, начиная с Java 7, для автоматического закрытия ресурсов.', 'Wersatil'),
    ('Что происходит при throw в finally?', 'Исключение из finally заменяет любое исключение из try/catch.', 'Wersatil'),
    ('Как проверить тип исключения в catch?', 'Использовать instanceof или отдельный catch для типа.', 'Wersatil'),
    ('Почему checked-исключения считаются безопасными?', 'Они заставляют программиста явно обрабатывать ошибки.', 'Wersatil'),
    ('Почему unchecked-исключения считаются удобными?', 'Не требуют явной обработки, уменьшают boilerplate.', 'Wersatil'),
    ('Можно ли наследовать от Error?', 'Можно, но обычно не рекомендуется.', 'Wersatil'),
    ('В чем разница между Error и Exception?', 'Error — серьёзные ошибки JVM, Exception — ошибки приложения.', 'Wersatil');