INSERT INTO question (question_text, answer, author) VALUES
    ('Что такое Checked-исключения и от каких классов они наследуются?', 'Checked-исключения — это исключения, которые проверяются компилятором и должны быть обработаны или объявлены через throws. Ими являются все наследники Exception, кроме RuntimeException.', 'Wersatil'),
    ('Что такое unchecked-исключения?', 'Unchecked-исключения — это RuntimeException и его подклассы, их можно не обрабатывать.', 'Wersatil'),
    ('Приведите 3 примера checked-исключений?', 'IOException, SQLException, ClassNotFoundException.', 'Wersatil'),
    ('Приведите 3 примера unchecked-исключений?', 'NullPointerException, ArrayIndexOutOfBoundsException, IllegalArgumentException.', 'Wersatil'),
    ('Что произойдет, если checked-исключение не обработано?', 'Компилятор выдаст ошибку, код не скомпилируется.', 'Wersatil'),
    ('Что произойдет, если unchecked-исключение не обработано?', 'Программа может упасть во время выполнения.', 'Wersatil'),
    ('Можно ли использовать try без catch?', 'Да, если есть блок finally.', 'Wersatil'),
    ('Можно ли использовать несколько catch для одного try?', 'Да, можно перехватывать разные типы исключений.', 'Wersatil'),
    ('Что такое multi-catch?', 'Использование одного catch для нескольких типов исключений через |.', 'Wersatil'),
    ('Какова разница между throw и throws?', 'throw — выбрасывает исключение, throws — объявляет возможность выбрасывания его для метода.', 'Wersatil'),
    ('Можно ли throw несколько исключений одновременно?', 'Нет, throw выбрасывает одно исключение.', 'Wersatil'),
    ('Можно ли создать свое checked-исключение и как это сделать? А unchecked-исключение?', 'Checked - наследовать от Exception и определить конструктор. Unchecked - то же самое, но от RuntimeException.', 'Wersatil'),
    ('Можно ли убрать throws при переопределении?', 'Да, если метод не выбрасывает checked-исключения.', 'Wersatil'),
    ('Можно ли ловить unchecked-исключения?', 'Да, можно, но не обязательно.', 'Wersatil'),
    ('Если блоков catch несколько, есть ли разница, в каком порядке их писать?', 'Да, нужно писать от более специфичных исключений к более общим. Иначе будет ошибка компиляции.', 'Wersatil'),
    ('Что произойдет, если указать return и в try, и в catch, и в finally?', 'Будет возвращено значение из finally, остальные проигнорируются.', 'Wersatil'),
    ('Что делает printStackTrace() у исключения?', 'Выводит стек вызовов до момента выброса исключения.', 'Wersatil'),
    ('Можно ли наследовать несколько исключений одновременно?', 'Нет, Java не поддерживает множественное наследование классов.', 'Wersatil'),
    ('Можно ли создать исключение без сообщения?', 'Да, можно использовать конструктор по умолчанию.', 'Wersatil'),
    ('Можно ли вызвать super() в своём исключении?', 'Да, чтобы передать сообщение или причину в родительский класс.', 'Wersatil'),
    ('Что такое chained exceptions?', 'Механизм выбрасывания исключения, которое хранит в себе ссылку на другое исключение. Например мы отлавливаем несколько исключений и выбрасываем другое. Вот в выбрасываемом мы можем указать, какое конкретно исключение мы поймали.', 'Wersatil'),
    ('Почему finally важен при работе с ресурсами?', 'Он гарантирует освобождение/закрытие ресурсов, даже если произошло исключение.', 'Wersatil'),
    ('Что такое try-with-resources и какой у него синтаксис?', 'Конструкция, позволяющая автоматически закрывать ресурсы (аналог finallY). Для ее использования нужно объявить ресурс в круглых скобках после "try".', 'Wersatil'),
    ('Что произойдет, если выбросить исключение и в try, и в catch, и в finally?', 'Исключение из finally заменит все исключения из try/catch.', 'Wersatil'),
    ('Какими двумя способами можно сделать разную обработку разных исключения в блоке try-catch?', 'Использовать if (instanceof...) или несколько разных catch.', 'Wersatil'),
    ('Можно ли наследовать от Error?', 'Синтаксически возможно, но не рекомендуется, потому что, если его не ловить через try-catch, то программа завершится аварийно, а если ловить, то это будет плохой практикой. .', 'Wersatil'),
    ('В чем разница между Error и Exception?', 'Error — серьёзные ошибки JVM, Exception — ошибки приложения.', 'Wersatil');