INSERT INTO question (question_text, answer, author) VALUES
    ('Чем отличается Thread от Runnable?', 'Thread — это класс для создания потока, Runnable — интерфейс с методом run(), используемый для задания задачи потоку.', 'Wersatil'),
    ('Как создать и запустить поток через Thread?', 'Создать объект Thread и передать ему Runnable, затем вызвать start().', 'Wersatil'),
    ('Можно ли запустить один и тот же Thread дважды?', 'Нет, после завершения потока его нельзя запустить снова.', 'Wersatil'),
    ('Можно ли синхронизировать метод?', 'Да, объявив метод с synchronized, весь метод блокируется.', 'Wersatil'),
    ('Что такое volatile?', 'Это модификатор поля, который гарантирует видимость изменений переменной между потоками, запрещая ее кэшировать..', 'Wersatil'),
    ('Что делает thread.interrupt() и для чего его вызывать?', 'thread.interrupt() нужен не для принудительной остановки потока, а для сигнализации потоку, что его просят завершиться или изменить поведение. Это делается для того, чтобы поток не уснул навсегда.', 'Wersatil'),
    ('Когда использовать volatile?', 'Для флагов и простых переменных, которые читаются и пишутся без атомарных операций, если нужно, чтобы переменная не кэшировалась..', 'Wersatil'),
    ('В чем разница между volatile и synchronized?', 'Volatile не обеспечивает атомарность, только видимость.', 'Wersatil'),
    ('Что такое deadlock?', 'Deadlock — ситуация, когда два или более потоков ждут друг друга бесконечно, блокируя ресурсы.', 'Wersatil'),
    ('Что такое livelock?', 'Livelock — ситуация, когда потоки активно меняют состояние, но задача не продвигается.', 'Wersatil'),
    ('Как предотвратить deadlock?', 'Использовать упорядоченное захватывание блокировок, таймауты или Lock.tryLock().', 'Wersatil'),
    ('Что такое ExecutorService?', 'ExecutorService — интерфейс для управления потоками и пулами потоков.', 'Wersatil'),
    ('Как создать пул потоков?', 'Использовать Executors.newFixedThreadPool(n) или другие фабрики Executors.', 'Wersatil'),
    ('Что делает метод submit() у ExecutorService?', 'Отправляет задачу на выполнение и возвращает Future.', 'Wersatil'),
    ('Что делает метод shutdown()?', 'Останавливает прием новых задач и завершает выполнение текущих.', 'Wersatil'),
    ('Что делает метод shutdownNow()?', 'Пытается прервать все активные задачи и возвращает список ожидающих задач.', 'Wersatil'),
    ('Что такое Callable?', 'Callable — интерфейс с методом call(), который что-нибудь возвращает и может выбрасывать исключение.', 'Wersatil'),
    ('Чем Callable отличается от Runnable?', 'Callable возвращает значение и может бросать checked-исключения, Runnable нет.', 'Wersatil'),
    ('Что такое Future?', 'Future — объект, который представляет результат асинхронной операции и позволяет получить результат или проверить завершение.', 'Wersatil'),
    ('Что такое CompletableFuture?', 'CompletableFuture — расширение Future с поддержкой цепочек, асинхронных и комбинированных операций.', 'Wersatil'),
    ('Можно ли комбинировать несколько CompletableFuture для совершения с ними каких-то действий?', 'Да, через методы thenCombine, allOf, anyOf и другие.', 'Wersatil'),
    ('Что такое CopyOnWriteArrayList?', 'Список, который копирует массив при модификации, безопасен для чтения и итерации в нескольких потоках.', 'Wersatil'),
    ('Какие недостатки у использования synchronizedMap вместо ConcurrentHashMap?', 'Уменьшение производительности, особенно при большом количестве потоков.', 'Wersatil'),
    ('Что такое ReentrantLock?', 'Lock, позволяющий вручную захватывать и освобождать блокировку, поддерживает повторный вход в блокировку.', 'Wersatil'),
    ('В чем преимущества ReentrantLock перед synchronized?', 'Больше возможностей: таймауты, tryLock(), interruptible lock, возможность выбора порядка блокировок.', 'Wersatil'),
    ('Что такое Condition в многопоточности?', 'Объект, связанный с ReentrantLock для ожидания и сигнализации, аналог wait/notify.', 'Wersatil'),
    ('Можно ли прервать поток с помощью interrupt()?','Нет. interrupt() выставляет флаг прерывания, поток должен проверять его сам.', 'Wersatil'),
    ('Что делает метод isInterrupted() в многопоточности?','Проверяет флаг прерывания потока.', 'Wersatil'),
    ('Что делает метод interrupted() в многопоточности?','Возвращает флаг прерывания текущего потока и сбрасывает его.', 'Wersatil'),
    ('Что такое fork/join?', 'Фреймворк для параллельного выполнения задач с разделением на подзадачи.', 'Wersatil'),
    ('Чем CopyOnWriteArrayList отличается от ArrayList?', 'ArrayList не потокобезопасен, CopyOnWriteArrayList безопасен для чтения и итерации.', 'Wersatil'),
    ('Что делает метод put() в BlockingQueue и создает ли он блокировку?', 'Добавляет элемент в очередь, блокируя поток если очередь полна.', 'Wersatil'),
    ('Что делает метод take() в BlockingQueue и создает ли он блокировку?', 'Извлекает элемент из очереди, блокируя поток если очередь пуста.', 'Wersatil'),
    ('Какими четырьмя способами можно создать потоки в Java?', 'Да, через ExecutorService, ForkJoinPool или CompletableFuture.', 'Wersatil');