INSERT INTO question (question_text, answer, author) VALUES
    ('Что такое @Controller в Spring?', 'Аннотация для обозначения класса как Spring MVC контроллера, который возвращает view.', 'Wersatil'),
    ('Что такое @RestController?', 'Комбинация @Controller и @ResponseBody, контроллер возвращает данные (JSON/ XML) вместо view.', 'Wersatil'),
    ('Чем @Controller отличается от @RestController?', '@Controller возвращает view, @RestController возвращает данные напрямую в теле ответа.', 'Wersatil'),
    ('Что делает @RequestMapping?', 'Позволяет настроить URL-путь и методы HTTP для обработки запроса.', 'Wersatil'),
    ('Что делает @GetMapping?', 'Сокращение для @RequestMapping с методом GET.', 'Wersatil'),
    ('Что делает @PostMapping?', 'Сокращение для @RequestMapping с методом POST.', 'Wersatil'),
    ('Что делает @PutMapping?', 'Сокращение для @RequestMapping с методом PUT.', 'Wersatil'),
    ('Что делает @DeleteMapping?', 'Сокращение для @RequestMapping с методом DELETE.', 'Wersatil'),
    ('Чем отличается @RequestParam от @PathVariable?', '@RequestParam получает значения из query-параметров, @PathVariable — из пути URL.', 'Wersatil'),
