INSERT INTO question (question_text, answer, author) VALUES
    ('Что делает аннотация @Entity?', 'Обозначает класс как сущность JPA, которая будет отображаться в таблицу базы данных.', 'Wersatil'),
    ('Что делает аннотация @Table?', 'Позволяет задать имя таблицы и другие параметры для сущности.', 'Wersatil'),
    ('Что такое @Id?', 'Обозначает поле как первичный ключ сущности.', 'Wersatil'),
    ('Что такое @GeneratedValue?', 'Позволяет автоматически генерировать значения первичного ключа.', 'Wersatil'),
    ('Что такое @OneToMany?', 'Связь один-ко-многим, где одна сущность связана с коллекцией других сущностей.', 'Wersatil'),
    ('Что такое @ManyToOne?', 'Связь многие-к-одному, где много сущностей ссылаются на одну родительскую.', 'Wersatil'),
    ('Чем @OneToMany отличается от @ManyToOne?', '@OneToMany на родителе, @ManyToOne на дочерней стороне; это два направления одной связи.', 'Wersatil'),
    ('Что такое mappedBy в @OneToMany?', 'Указывает поле в дочерней сущности, которое владеет связью.', 'Wersatil'),
    ('Что такое FetchType.LAZY?', 'Связанные сущности загружаются только при первом обращении, а не сразу.', 'Wersatil'),
    ('Что такое FetchType.EAGER?', 'Связанные сущности загружаются сразу вместе с основной сущностью.', 'Wersatil'),
    ('Какая разница между LAZY и EAGER?', 'LAZY — отложенная загрузка, EAGER — немедленная загрузка связанных данных.', 'Wersatil'),
    ('Что такое CascadeType?', 'Определяет, какие операции должны каскадироваться от родителя к дочерним сущностям.', 'Wersatil'),
    ('Примеры CascadeType?', 'PERSIST, MERGE, REMOVE, REFRESH, DETACH, ALL.', 'Wersatil'),
    ('Что делает CascadeType.PERSIST?', 'При сохранении родителя автоматически сохраняются дочерние сущности.', 'Wersatil'),
    ('Что делает CascadeType.REMOVE?', 'При удалении родителя автоматически удаляются дочерние сущности.', 'Wersatil'),
    ('Что делает CascadeType.MERGE?', 'При объединении состояния родителя изменения применяются и к дочерним сущностям.', 'Wersatil'),
    ('Что такое N+1 problem?', 'Ситуация, когда при выборке родительских сущностей делается N дополнительных запросов для дочерних.', 'Wersatil'),
    ('Почему возникает N+1 problem?', 'Из-за FetchType.LAZY и последующих обращений к коллекциям без join fetch.', 'Wersatil'),
    ('Как решить N+1 problem?', 'Использовать join fetch в JPQL или EntityGraph, или EAGER fetch, но осторожно.', 'Wersatil'),
    ('Что делает EntityManager?', 'Интерфейс JPA для управления жизненным циклом сущностей и выполнения запросов.', 'Wersatil'),
    ('Пример методов EntityManager?', 'persist(), merge(), remove(), find(), createQuery().', 'Wersatil'),
    ('Что делает Repository в Spring Data JPA?', 'Интерфейс для работы с сущностями, автоматически реализует CRUD методы.', 'Wersatil'),
    ('Разница между EntityManager и Repository?', 'EntityManager — низкоуровневый интерфейс JPA, Repository — высокоуровневый Spring Data подход.', 'Wersatil'),
    ('Что делает метод save() в JpaRepository?', 'Сохраняет или обновляет сущность в базе данных.', 'Wersatil'),
    ('Что делает метод findById() в JpaRepository?', 'Возвращает сущность по первичному ключу, если существует.', 'Wersatil'),
    ('Что делает метод delete() в JpaRepository?', 'Удаляет сущность из базы данных.', 'Wersatil'),
    ('Что такое JPQL?', 'Java Persistence Query Language — объектно-ориентированный язык запросов к сущностям.', 'Wersatil'),
    ('Что такое TypedQuery?', 'Интерфейс для выполнения JPQL запросов с указанным типом результата.', 'Wersatil'),
    ('Что такое @JoinColumn?', 'Определяет колонку в таблице для связи с другой сущностью.', 'Wersatil'),
    ('Что такое orphanRemoval=true?', 'Автоматически удаляет дочерние сущности, которые больше не связаны с родителем.', 'Wersatil'),
    ('Что делает fetch join в JPQL?', 'Позволяет загружать связанные сущности одним запросом, решая N+1 проблему.', 'Wersatil'),
    ('Можно ли менять FetchType динамически?', 'Нет, FetchType задается на уровне аннотации, но можно использовать join fetch в запросах.', 'Wersatil'),
    ('Что делает метод persist()?', 'Сохраняет новую сущность в контекст персистентности.', 'Wersatil'),
    ('Что делает метод merge()?', 'Объединяет состояние detached сущности с контекстом персистентности.', 'Wersatil'),
    ('Что делает метод remove()?', 'Удаляет сущность из базы данных.', 'Wersatil'),
    ('Что делает метод find()?', 'Находит сущность по первичному ключу.', 'Wersatil'),
    ('Что такое detached сущность?', 'Сущность, которая была извлечена из контекста персистентности и не отслеживается.', 'Wersatil'),
    ('Что такое managed сущность?', 'Сущность, которая отслеживается контекстом персистентности, изменения автоматически синхронизируются.', 'Wersatil'),
    ('Что такое dirty checking?', 'Механизм JPA, который автоматически отслеживает изменения managed сущностей и сохраняет их в базе.', 'Wersatil'),
    ('Как избежать N+1 при OneToMany?', 'Использовать fetch join, @EntityGraph или batch size через Hibernate.', 'Wersatil'),
    ('Можно ли использовать CascadeType с ManyToOne?', 'Да, можно, но осторожно, обычно не используют REMOVE для ManyToOne.', 'Wersatil'),
    ('Что такое bidirectional связь?', 'Связь, где обе сущности ссылаются друг на друга через @OneToMany/@ManyToOne и mappedBy.', 'Wersatil'),
    ('Что такое unidirectional связь?', 'Связь, где только одна сущность знает о другой.', 'Wersatil'),
    ('Почему bidirectional связь полезна?', 'Позволяет навигацию в обе стороны и удобное управление коллекциями.', 'Wersatil'),
    ('Как избежать LazyInitializationException?', 'Использовать fetch join, открыть сессию до доступа к ленивым коллекциям, или EAGER fetch.', 'Wersatil'),
    ('Что такое @ManyToMany?', 'Связь многие-ко-многим между сущностями через join таблицу.', 'Wersatil'),
    ('Пример использования @ManyToMany?', '@ManyToMany(mappedBy="roles") private Set<User> users;', 'Wersatil');