INSERT INTO question (question_text, answer, author) VALUES
    ('Что такое Integration тест?', 'Тест, проверяющий взаимодействие нескольких компонентов или слоев приложения.', 'Wersatil'),
    ('Что такое JUnit 5?', 'Современный фреймворк для написания и запуска тестов на Java.', 'Wersatil'),
    ('Основные модули JUnit 5?', 'JUnit Platform, JUnit Jupiter, JUnit Vintage.', 'Wersatil'),
    ('Что делает @Disabled в JUnit 5?', 'Отключает тест, который не нужно выполнять.', 'Wersatil'),
    ('Что такое Assertions в JUnit 5?', 'Методы для проверки ожидаемых значений, например assertEquals, assertTrue.', 'Wersatil'),
    ('Что такое Mockito?', 'Фреймворк для создания заглушек (mock) и проверки взаимодействий с зависимостями.', 'Wersatil'),
    ('Что делает @InjectMocks?', 'Внедряет моки в тестируемый объект.', 'Wersatil'),
    ('Что такое ArgumentCaptor?', 'Инструмент из библиотеки Mockito, который используется в unit-тестах (обычно на Java), чтобы перехватывать и проверять аргументы, переданные в метод mock-объекта.', 'Wersatil'),
    ('Что такое @SpringBootTest?', 'Аннотация для интеграционного тестирования Spring Boot приложения с полной загрузкой контекста.', 'Wersatil'),
    ('Можно ли запускать тест с определенным профилем?', 'Да, через @ActiveProfiles("test") в Spring Boot тестах.', 'Wersatil'),
    ('Чем @Mock отличается от @MockBean?', '@Mock — обычный мок для Unit теста, @MockBean — мок в Spring контексте для Integration теста.', 'Wersatil'),
    ('Можно ли комбинировать @SpringBootTest и @MockBean?', 'Да, для интеграционных тестов с подменой зависимостей на моки.', 'Wersatil'),
    ('Что такое TestRestTemplate?', 'Утилита для выполнения HTTP-запросов в интеграционных тестах Spring Boot.', 'Wersatil'),
    ('Что такое @WebMvcTest?', 'Аннотация для тестирования только слоя веб-контроллеров без полной загрузки контекста.', 'Wersatil'),
    ('Чем @DataJpaTest отличается от @SpringBootTest?', '@DataJpaTest загружает только JPA компоненты и базу данных для тестирования репозиториев.', 'Wersatil'),
    ('Что такое @AutoConfigureMockMvc?', 'Настройка MockMvc для тестирования MVC-контроллеров без реального сервера.', 'Wersatil'),
    ('Что такое MockMvc?', 'Объект для тестирования HTTP-запросов и ответов контроллеров Spring MVC.', 'Wersatil'),
    ('Что делает @Transactional в тестах?', 'Оборачивает тест в транзакцию и откатывает изменения после его завершения.', 'Wersatil'),
    ('Что делает @ExtendWith?', 'Подключает расширение JUnit 5, например для Mockito или Spring.', 'Wersatil'),
    ('Что такое @TestInstance?', 'Аннотация JUnit 5 (Jupiter), которая управляет жизненным циклом экземпляра тестового класса. По умолчанию JUnit 5 создаёт новый объект тестового класса для каждого тестового метода, но @TestInstance позволяет это изменить. Варианты: PER_CLASS или PER_METHOD.', 'Wersatil'),
    ('Что такое @ParameterizedTest?', 'Позволяет запускать тест с разными параметрами.', 'Wersatil'),
    ('Что делает @Captor?', 'Создает ArgumentCaptor для проверки аргументов методов мока.', 'Wersatil'),
    ('Чем doReturn().when() отличается от when().thenReturn()?', 'При when().thenReturn() вызывается реальный метод, при doReturn().when() - нет.', 'Wersatil'),
    ('Что делает lenient() в Mockito?', 'Ослабляет строгую проверку моков, чтобы Mockito не падал из-за неиспользованных заглушек.', 'Wersatil'),
    ('Что делает reset() в Mockito?', 'Сбрасывает настройки мока к исходному состоянию.', 'Wersatil'),
    ('Можно ли мокать final классы и методы в Mockito?', 'Да, начиная с Mockito 2+ с включением mock-maker-inline.', 'Wersatil'),
    ('Что такое BDDMockito?', 'Часть библиотеки Mockito, которая позволяет писать тесты в стиле BDD (Behavior-Driven Development) — «поведение системы» вместо «вызовов методов». Технически это обёртка над обычным Mockito, без новой логики — меняется только синтаксис и читаемость.', 'Wersatil'),
    ('Что делает verifyNoMoreInteractions в Mockito?', 'Проверяет, что больше нет вызовов мока кроме проверенных.', 'Wersatil'),
    ('Что такое InOrder в Mockito?', 'Позволяет проверять порядок вызовов методов мока.', 'Wersatil'),
    ('Что такое spyBean в Mockito?', 'Аннотация для spy над Spring Bean в интеграционных тестах.', 'Wersatil');